package javaprj;

public class HwstarD {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int a = 0; a < 11; a++) {
		      
		      for(int c = 9; c >= a; c--) {
		        System.out.print(" ");
		      }
		      for(int b = 0; b <= a; b++) {
		        System.out.print("*");
		      }
		      System.out.println();
		    }

	}

}
